export const retentionHandler = () => {
  console.log("teste")
  $('#check_retention:not(.change-binded)').addClass('change-binded').on('change', (evt) => {
    const el = $(evt.currentTarget)

    if(el.prop('checked')) { return ajax(el.data('new-url'), 'get') }
    if(!el.data('destroy-url')) { return $('#retention-form').html(''); }
    if (confirm($('body').data('confirm-message'))) {
      const overwriteField = $('input[type=checkbox][name*=descendants_retention_overwrite]');
      const params = { descendants_retention_overwrite: overwriteField.prop('checked') };
      return ajax(el.data('destroy-url'), 'delete', params);
    }

    el.prop('checked', true);
  })
}
